select `rent`.`rent`as `rent` ,
concat ('[',`rent`.`invoice` ,',','"',`client`.`name` ,'/-/',`period`.`year` ,'/-/',`period`.`month` ,'/-/',`invoice`.`is_valid` ,'"',']') as `invoice_id`,concat ('[',`rent`.`agreement` ,',','"',`client`.`name` ,'/-/',`room`.`uid` ,'/-/',`agreement`.`start_date` ,'"',']') as `agreement_id`,`rent`.`room_no` as `room_no`,`rent`.`price` as `price`,`rent`.`agreement_start_date` as `agreement_start_date`,`rent`.`rental_period` as `rental_period`,`rent`.`factor` as `factor`,`rent`.`amount` as `amount` from `rent` INNER JOIN `invoice` ON rent.invoice=invoice.invoice INNER JOIN `client` ON invoice.client=client.client INNER JOIN `period` ON invoice.period=period.period INNER JOIN `agreement` ON rent.agreement=agreement.agreement 
INNER JOIN `room` ON agreement.room=room.room